# sticking to rails and rspec-rails APIs to keep things simple and stable.

RSpec.describe "/seasons", type: :request do
  let!(:user) { create(:user) }

  before(:each) do
    sign_in_as(user)
  end

  let(:valid_attributes) { valid_season_attributes }
  let!(:season) { create(:season, valid_attributes) }
  let(:invalid_attributes) { invalid_season_attributes }
  let!(:invalid_season) { build(:season, invalid_attributes) }

  describe "GET /index" do
    it "renders a successful response" do
      get seasons_url
      expect(response).to be_successful
    end
  end

  describe "GET /show" do
    it "renders a successful response" do
      get season_url(season)
      expect(response).to be_successful
    end
  end

  describe "GET /new" do
    it "renders a successful response" do
      get new_season_url
      expect(response).to be_successful
    end
  end

  describe "GET /edit" do
    it "renders a successful response" do
      get edit_season_url(season)
      expect(response).to be_successful
    end
  end

  describe "POST /create" do
    context "with valid parameters" do
      it "creates a new Season" do
        expect {
          post seasons_url, params: { season: valid_attributes }
        }.to change(Season, :count).by(1)
      end

      it "redirects to the created season" do
        post seasons_url, params: { season: valid_attributes }
        expect(response).to redirect_to(season_url(Season.last))
      end
    end

    context "with invalid parameters" do
      it "does not create a new Season" do
        expect {
          post seasons_url, params: { season: invalid_attributes }
        }.to change(Season, :count).by(0)
      end

      it "renders a response with 422 status (i.e. to display the 'new' template)" do
        post seasons_url, params: { season: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_content)
      end
    end
  end

  describe "PATCH /update" do
    context "with valid parameters" do
      let(:new_attributes) { edit_season_attributes }

      it "updates the requested season" do
        expect(season.title).to_not eq(new_attributes[:title])

        patch season_url(season), params: { season: new_attributes }

        season.reload
        expect(season.title).to eq(new_attributes[:title])
      end

      it "redirects to the season" do
        patch season_url(season), params: { season: new_attributes }
        season.reload
        expect(response).to redirect_to(season_url(season))
      end
    end

    context "with invalid parameters" do
      it "renders a response with 422 status (i.e. to display the 'edit' template)" do
        patch season_url(season), params: { season: invalid_attributes }
        expect(response).to have_http_status(:unprocessable_content)
      end
    end
  end

  describe "DELETE /destroy" do
    it "destroys the requested season" do
      expect {
        delete season_url(season)
      }.to change(Season, :count).by(-1)
    end

    it "redirects to the seasons list" do
      delete season_url(season)
      expect(response).to redirect_to(seasons_url)
    end
  end

  describe "Turbo Stream responses" do
    let(:headers) { { "ACCEPT" => "text/vnd.turbo-stream.html" } }

    describe "GET /new" do
      it "returns a turbo stream response" do
        get new_season_url, headers: headers
        expect(response.media_type).to eq("text/vnd.turbo-stream.html")
        expect(response.body).to include("turbo-stream")
      end
    end

    describe "POST /create" do
      it "returns a turbo stream response on success" do
        post seasons_url, params: { season: valid_attributes }, headers: headers
        expect(response.media_type).to eq("text/vnd.turbo-stream.html")
        expect(response.body).to include("turbo-stream")
        expect(response.body).to include("prepend")
      end

      it "returns a turbo stream response on failure" do
        post seasons_url, params: { season: invalid_attributes }, headers: headers
        expect(response.media_type).to eq("text/vnd.turbo-stream.html")
        expect(response.body).to include("turbo-stream")
      end
    end

    describe "GET /edit" do
      it "returns a turbo stream response" do
        season = Season.create! valid_attributes
        get edit_season_url(season), headers: headers
        expect(response.media_type).to eq("text/vnd.turbo-stream.html")
        expect(response.body).to include("turbo-stream")
      end
    end

    describe "PATCH /update" do
      it "returns a turbo stream response on success" do
        season = Season.create! valid_attributes
        patch season_url(season), params: { season: valid_attributes }, headers: headers
        expect(response.media_type).to eq("text/vnd.turbo-stream.html")
        expect(response.body).to include("turbo-stream")
      end

      it "returns a turbo stream response on failure" do
        season = Season.create! valid_attributes
        patch season_url(season), params: { season: invalid_attributes }, headers: headers
        expect(response.media_type).to eq("text/vnd.turbo-stream.html")
        expect(response.body).to include("turbo-stream")
      end
    end

    describe "DELETE /destroy" do
      it "returns a turbo stream response" do
        season = Season.create! valid_attributes
        delete season_url(season), headers: headers
        expect(response.body).to include("turbo-stream")
        expect(response.body).to include("remove")
      end
    end
  end
end
